mapper.select:
SELECT e.id,e.name,e.`phone`,e.`createDate`,d.`name` AS departmentName,e.extensionrandomcode FROM employee e LEFT JOIN department d ON e.`departmentid` = d.`id` WHERE e.`statu`=1
and e.name like #{name,jdbcType=VARCHAR}
and roleid = (SELECT r.`id` FROM role r WHERE r.`descript`=#{roleDescriptName,jdbcType=VARCHAR}) ORDER BY e.`createDate` DESC

SELECT t.* FROM ( SELECT e.`id` AS empId,e.`name` AS empName,COUNT(o.`id`) AS allCount FROM employee e LEFT JOIN orders o ON e.`id` = o.`empid` WHERE e.`statu` = 1 AND e.`roleid` = (SELECT r.`id` FROM role r WHERE r.`descript`='EMPLOYEE' AND r.`statu`=1) AND o.`statu`=1
and e.name = #{empName,jdbcType=VARCHAR}
GROUP BY e.`id`,e.`name` ORDER BY COUNT(*) DESC ) t
